import React from 'react';
import TodoList from './components/TodoList';
import NewTodo from './components/NewTodo';

const App: React.FC = () => {
  const todos = [{id: 0, text: 'Finish the course'}];

  const addTodo = (todoValue: string) => {
    // console.log(todos);
    todos.push({id: todos.length, text: todoValue });
    console.log(todos);
  }

  return (
    <div className="App">
      <NewTodo addTodo={(newTodo: string) => { addTodo(newTodo); }} />
      <TodoList items={todos} />
    </div>
  );
}

export default App;
