import React, { useState } from 'react';
import TodoList from './components/TodoList';
import NewTodo from './components/NewTodo';

const App: React.FC = () => {
  type Todo = {
    id: number;
    text: string;
  };

  const initialTodos: Todo[] = [
    {
      id: 0,
      text: 'Finish the course'
    }
  ];

  const [todos, setTodos] = useState(initialTodos);
  const [isSettingTodos, setIsSettingTodos] = useState(false);

  const addTodo = (todoValue: string) => {
    setTodos(todos.concat(...todos, { id: todos.length, text: todoValue } ))

    console.log(todos);
    setIsSettingTodos(!isSettingTodos);
  }

  return (
    <div className="App">
      <NewTodo addTodo={(newTodo: string) => { addTodo(newTodo); }} />
      <TodoList key={todos.length} items={todos} />
    </div>
  );
}

export default App;
