import React from 'react';
import TodoList from './components/TodoList';
import NewTodo from './components/NewTodo';

const App: React.FC = () => {
  const todos = [{id: 't1', text: 'Finish the course'}];

  const addTodo: string = (todoValue) => {
    todos.push({id: 't2', text: todoValue });
  }

  return (
    <div className="App">
      <NewTodo addTodo={(newTodo) => { addTodo(newTodo); }} />
      <TodoList items={todos} />
    </div>
  );
}

export default App;
